import React, { useState, useEffect } from 'react';
import CarouselCard from './CarouselCard'
import { carouselData } from './CarouselData';

const Carousel = () => {

    const [focus, setFocus] = useState(1);

    const slides = document.getElementsByClassName('carousel-card');
    console.log(slides)

    useEffect(() => {
        showSlides(focus)
        console.log(slides[0])
    }, [slides])

    const slideNav = (n) => {
        showSlides(focus + n)
        setFocus(focus + n)
    }

    const showSlides = (n) => {
        let i;
        console.log('n', n, 'focus', focus)
        //dot querySelector here
        if (n > 3){
            setFocus(1)
        }
        if (n < 1){
            setFocus(slides.length)
        }
        for(i=0; i < slides.length; i++){
            slides[i].style.display = 'none'
        }
        slides[focus -1].style.display = 'block'
    }

    return(
        <div className='caousel'>
            <div className='carousel-cards'>
            {carouselData.map(content => <CarouselCard content={content}/> )}
            </div>
            <div className='carousel-buttons'>
                <button onClick={()=>slideNav(-1)}>Back</button>
                <button onClick={()=>slideNav(1)}>Next</button>
            </div>
        </div>
    )
}

export default Carousel;
