import React, { useState } from 'react';

const Form = () => {

    const [email, setEmail] = useState({
        body: '',
        name: '',
        email: ''
    })

    const [sent, setSent] = useState(false)

  const handleChange = (event) => {
    setEmail({
        ...email,
        [event.target.name]: event.target.value
  })
}

  const handleSubmit = (event) => {
	const templateId = 'template_yWIY0O7k';

	sendFeedback(templateId, {message_html: email.body, from_name: email.name, reply_to: email.email})
  }

  const sendFeedback = (templateId, variables) => {
	window.emailjs.send(
  	'gmail', templateId,
  	variables
  	).then(res => {
        console.log('Email successfully sent!')
        setSent(true)
  	})
  	.catch(err => console.error(err))
  }

	return (
  	<div className="contact">
    	<h2>Get in Touch</h2>
        {sent ? <h3>Thanks for reaching out! I'll get back to you as soon as possible</h3> : 
        <form>
    	<div className='contact-top'>
        <input 
            name="name"
            value={email.name}
            placeholder='Name'
            onChange={handleChange}
            required
        />
           <input 
            name="email"
            value={email.email}
            placeholder='Email'
            onChange={handleChange}
            required
        />
        </div>
      	<textarea
        	name="body"
        	onChange={handleChange}
        	placeholder="Hey Juliette,"
        	required
        	value={email.body}
      	/>
    	<button onClick={handleSubmit}> Send </button>
  	</form>}
      </div>
	)
  }

export default Form;
