import React, { useState, useEffect } from 'react';
import CarouselCard from './CarouselCard'
import { carouselData } from './CarouselData';

const Carousel = () => {

    const [active, setActive] = useState(0);

    const selectSlide = (index) => setActive(index);

    const back = (e) => {
        e.preventDefault()
        let index = active
        let slidesLength = carouselData.length
        if(index < 1){
            index = slidesLength
        }
        --index
        setActive(index)
    }

    const next = (e) => {
        e.preventDefault()
        let index = active
        let slidesLength = carouselData.length - 1
        if(index === slidesLength){
            index = -1
        }
        ++index
        setActive(index)
    }

    return(
        <div className='carousel'>
            <div className='carousel-cards'>
            {carouselData.map(content => <CarouselCard content={content}/> )}
            </div>
            <div className='carousel-buttons'>
                <button onClick={(e)=>back(e)}>Back</button>
                <button onClick={(e)=>next(e)}>Next</button>
            </div>
        </div>
    )
}

export default Carousel;

