import React, { useState } from 'react';

const Carousel = () => {

    const [focus, setFocus] = useState('');

    return(
        <div className='caousel'>
            {function(){
                switch(focus){
                    case 'one':
                        return(<>
                          <div className='project-section-text'>
                            <h3>Quality Hub - Interview Q</h3>
                            <p>Built with a team of five other developers and a UX designer over an 8-week sprint, Quality Hub-Interview Q highlights my skills with Javascript/React, GraphQL, Apollo, Prisma, and websockets. I worked on both front and back end on this prroject. Highlights of my contribution include a fully custom, built-from-scratch calendar on the front endand scheduling system and helping develop a federated microservice architechture on the backend</p>
                        </div>
                        <div className='project-section-image'>
                            <img src='./images/qualityhub.png'/>
                        </div>
                        </>)
                    case 'two':
                        return(<>
                          <div className='project-section-text'>
                            <h3>Eletric Boogaloo</h3>
                            <p>A short, nostalgic top down game built in four days with two other developers. On this project, I worked with Python/Django, and Javascript/React. Notable contributions include learning the Django framework on the fly and developing an algorithm to map out worlds quickly and efficiently, along with creating a beautiful and fun user interface.</p>
                        </div>
                        <div className='project-section-image'>
                            <img src='./images/electricboogaloo.png'/>
                        </div>
                        </>)
                }
            }()}
        </div>
    )
}

export default Carousel;
